function get_subname(url,gid){
    var sub=$('#sub');
    sub.empty();
    sub.append("<option value=''>--请选择细分类--</option>");
    $.post(url,{group_id:gid},function(data){
        if(data.status){
            var data_info=data.info;
            for(var i = 0 ; i<data_info.length;i++){
                var id = data_info[i].id;
                var name = data_info[i].name;
                sub.append("<option value='"+id+"'>"+name+"</option>");
            }
        }else{
            alert(data.info);
        }
    });
}
